// Core theme types

export interface GeneratedStyle {
  id: string;
  prompt: string;
  timestamp: number;

  // Complete CSS generated by agent
  css: string;

  // SVG definitions for patterns, gradients, etc.
  svgDefs?: string;

  // Metadata
  metadata?: {
    mood?: string;
    colorPalette?: string;
    inspiration?: string;
  };
}

export interface StyleRequest {
  prompt: string;
  context?: {
    currentTheme?: GeneratedStyle;
    components?: string[];
  };
}

export interface ThemeContract {
  // Which theme properties this component uses
  uses: string[];

  // CSS classes this component provides for styling
  providesClasses?: string[];
}
